msg_val:
style() ::= <<
	body '{'
		font-family: sans-serif;
		background-color: #FFF;
		margin: 2rem; '}'
	footer '{' border-top: 2px solid #F7C819; color: #666; margin-top: 2rem; '}'
	body > p '{' font-size: 0.8rem; margin: 0.2rem 0 1rem 0; '}'
	footer > p '{' margin-top: 4px; margin-bottom: 4px; font-size: 0.8rem; '}'
	footer > p + p '{' border-top: 1px solid #CCC; padding-top: 2px; '}'
	h1 '{' font-size: 1.5rem; font-weight: 200; margin: 1rem 0 0 0; clear: both; '}'
	kbd '{' font-size: 1.1rem; font-weight: bold; '}'
	body > table '{' clear: both; '}'
	table '{' width: 100%; border-collapse: collapse; '}'
	table + table '{' margin-top: 3rem; '}'
	th.hr '{' border-top: 2px solid black; '}'
	td.hr '{' border-bottom: 1px solid #CCC; '}'
	td.space '{' height: 1rem; '}'
	th '{' text-align: left; width: 225px; '}'
	.addr p '{' margin: 0; '}'
	.addr p + p '{' margin-top: 0.2rem; '}'
	.draft '{' color: #c00; '}'
	.head '{' font-weight: bold; '}'
	.left '{' text-align: left; '}'
	.right '{' text-align: right; '}'
	.secondary '{' color: #666; '}'

	.from '{'
		float: left;
		width: 50%; '}'
	.from .logo '{' height: 2rem; margin: 0 0 1rem 0; '}'
	.from .addr '{' 
		color: #666;
		font-size: 0.8rem; '}'
	.info '{'
		float: right;
		width: 50%;
		margin-top: 3rem; 
		margin-bottom: 1rem; '}'
	.info td,
	.info th '{' vertical-align: top; padding: 0.2rem; '}'
	.info td:first-child '{'
		font-weight: 700;
		text-align: right;
		padding-right: 1rem; '}'

	.details > tbody > tr > th '{'
		font-weight: 200;
		font-size: 1.1rem; '}'
	.details tbody td,
	.details tbody th '{' 
		padding-top: 2px;
		padding-bottom: 2px;
		vertical-align: text-bottom;
		font-size:0.8rem; '}'
	.details th + th,
	.details td + td,
	.details td + td '{'
		padding-left: 0.5rem;
		text-align: right; '}'

	.totals '{'
		float: right;
		width: auto;
		margin-top: 1rem;
		margin-bottom: 1rem; '}'
	.totals tbody tr > * '{' font-size: 1rem; '}'
	.totals tbody tr > *:first-child '{' padding-right: 1rem; '}'
	.totals tbody tr:last-child > * '{'
		font-weight: 700; '}'

	.items thead th '{' vertical-align: top; '}'
	.items tbody td,
	.items tbody th,
	.items tfoot td,
	.items tfoot th '{'
		padding-top: 2px;
		padding-bottom: 2px;
		vertical-align: text-bottom;
		font-size:0.8rem; '}'
	.items th + th,
	.items td + td '{'
		padding-left: 0.5rem;
		text-align: right; '}'
	.items tbody td + td,
	.items tfoot td + td '{'
		font-size: 0.8rem;
		padding-left: 0.5rem;
		text-align: right; '}'
	.items th '{' width: auto; '}'
	.items th .unit '{'
		display: block;
		font-size: 0.8rem;
		color: #666;
		font-weight: 400;
		text-transform: lowercase; '}'

	@page '{'
		size: a4;
	'}'
>>
